import { useContext } from "react";
import classes from "./Cart.module.css";
import Modal from "../UI/Modal";
import CartContext from "../../store/Cart-context";
import CartItem from "./CartItem";

const Cart = (props) => {
  const cartCtx = useContext(CartContext);

  const onAddItemHandler = item => {};

  const onRemoveItemHandler = id => {};

  const totalAmoount = `$${cartCtx.totalAmount.toFixed(2)}`;
  const hasItem = cartCtx.totalAmount.length > 0 ;

  const CartItem = (
    <ul className={classes["cart-items"]}>
      {cartCtx.items.map((item) => (
        <CartItem  key={item.id} name={item.name} amount={item.amount} price={item.price} onAdd={onAddItemHandler} onRemove={onRemoveItemHandler} />
      ))}
    </ul>
  );

  return (
    <Modal onCloseModalBackdrop={props.onHideCart}>
      {CartItem}
      <div className={classes.total}>
        <span>{totalAmoount}</span>
        <span>35.92</span>
      </div>

      <div className={classes.actions}>
        <button className={classes["button--alt"]} onClick={props.onHideCart}>
          Close
        </button>
        {hasItem && <button className={classes.button}>Order</button>}
      </div>
    </Modal>
  );
};

export default Cart;
